export const randomNum=(t,e)=>Math.floor(Math.random()*(e-t+1))+t;export function timeTaken(t,e,...o){let n=performance.now();for(let r=0;r<e;r++)t(...o);let m=performance.now()-n;return m<1e3?m.toFixed(1)+"ms":(m/1e3).toFixed(1)+"s"}export function pitStop(t,e,...o){let n=[];for(let r of o)n.push({name:r.name,time:timeTaken(r,t,...e),output:r(...e)});return console.table(n)}
